import React from 'react';
import './App.css';
import Player from './components/Player';
// import Counter from './components/Counter';
import Header from './components/Header';
import AddPlayerComp from './components/AddPlayerComp';








class App extends React.Component{

  state= {
      players: [
          {name:"John",
          score:15,
          id:1},
          {name:"Ben",
          score:20,
          id:2},
          {name:"Jack",
          score:50,
          id:3},
          {name:"Jane",
          score:55,
          id:4},
          {name:"Lex",
          score:15,
          id:5},
          {name:"Rex",
          score:20,
          id:6},
          {name:"Pam",
          score:50,
          id:7},
          {name:"Liam",
          score:55,
          id:8}
      ]
  }

  deleteData=(x)=>{
      this.setState(prevState=>{

          return {

              players: prevState.players.filter(index=>index.id!==x)
          }
      }

      ,

      () => {
        this.players.updateItem(this.state);
     });
  }

  updateItem=()=>{
    {this.state.players.map(
      player=>
  <Player name ={player.name} score={player.score} key={player.id} id={player.id}/> )
  }}

  addPlayer=(nameX,scoreX,idX)=>{
      this.setState(prevState=>{
          return{
              players:prevState.players.push({name:"nameX",score:"scoreX",id:"idX"})
          }
      })}

      decrementScore(){
        if(this.state.score>0){

            this.setState((prevState)=>({
                score: prevState.score-1
            }))
        }
    }

    incrementScore(){
        this.setState((prevState)=>({
            score: prevState.score+1
        }))
    }
  render(){
      return(
          <div className="scoreboard">
              <Header title="Scoreboard" totalPlayers={this.state.players.length}/>
              {this.state.players.map(
                  player=>
              <Player name ={player.name} score={player.score} key={player.id} id={player.id} 
              deleteDataX = {this.deleteData} incrementScore={this.incrementScore} decrementScore={this.decrementScore}
              />
              )}
              <AddPlayerComp addPlayer={this.addPlayer}/>

          </div>
      )
  }
}


export default App;
