import React from "react";

const StopWatch = (props)=>{
    // const timer = document.getElementById('stopwatch');

    let hr = 0;
    let min = 0;
    let sec = 0;
    let stoptime = true;

startTimer =()=> {
  if (stoptime == true) {
        stoptime = false;
        timerCycle();
    }
}
stopTimer =()=> {
  if (stoptime == false) {
    stoptime = true;
  }
}

timerCycle=()=> {
    if (stoptime == false) {
    sec = parseInt(sec);
    min = parseInt(min);
    hr = parseInt(hr);

    sec = sec + 1;

    if (sec == 60) {
      min = min + 1;
      sec = 0;
    }
    if (min == 60) {
      hr = hr + 1;
      min = 0;
      sec = 0;
    }

    if (sec < 10 || sec == 0) {
      sec = '0' + sec;
    }
    if (min < 10 || min == 0) {
      min = '0' + min;
    }
    if (hr < 10 || hr == 0) {
      hr = '0' + hr;
    }

    timer.innerHTML = hr + ':' + min + ':' + sec;

    setTimeout("timerCycle()", 1000);
  }
}

resetTimer =()=> {
    timer.innerHTML = '00:00:00';
}

return (
    <div className="player">
        <div className="player-name">
        <button className="remove-player" onClick={()=>props.deleteDataX(props.id)}>Start timer</button>
        <div id="stopwatch"></div>
        </div>
        <Counter id={props.id} index={props.index} score={props.score} incrementScore={props.incrementScore} decrementScore={props.decrementScore}/>
    </div>
)

}
export default StopWatch
