                // }))
            })
            // console.log(Date.now())
            )}
            // if (this.state.sec < 10 || this.state.sec == 0) {
            // this.state.sec = '0' + this.state.sec;
            // }
            // if (this.state.min < 10 || this.state.min == 0) {
            // this.state.min = '0' + this.state.min}
            // if (this.state.hr < 10 || this.state.hr == 0) {
            // this.state.hr = '0' + this.state.hr;
            // }
        }

    handleStopwatch =()=>{
        console.log("handle watch")
        this.setState(prevState=>({
            isRunning: !prevState.isRunning,
            // sec:this.state.sec,

        }))
        console.log(this.state);
}


resetTimer =()=> {
    this.setState({
        sec:0,
        min:0,
        hr:0
    })
}

render (){
    return (
        <div className="stopwatch">
            <h2>Stopwatch</h2>
            <span className="stopwatch-time">{this.state.hr}:{this.state.min}:{this.state.sec}</span>
            <button className="" onClick={this.handleStopwatch}>{this.state.isRunning ? "Stop" : "Start"}</button>
            {/* <button className="" onClick={()=>this.stopTimer()}>Stop timer</button> */}
            <button className="" onClick={this.resetTimer}>Reset timer</button>
            <div className="stopwatch"></div>
            {/* <Counter id={props.id} index={props.index} score={props.score} incrementScore={props.incrementScore} decrementScore={props.decrementScore}/> */}
        </div>
    )

}


}
export default StopWatch
