import React from "react";

class StopWatch extends React.Component{
    state ={
        isRunning:false,
        timer: document.querySelector('.stopwatch'),
        hr: 0,
        min: 0,
        sec : 0,
        stoptime : true

    }
handleStopwatch =()=>{
    this.setState(prevState=>({
        isRunning: !prevState.isRunning
    }))
    console.log(this.isRunning);
}


startTimer =()=> {
  if (this.stoptime == true) {
        this.stoptime = false;
        this.timerCycle();
    }
}
stopTimer =()=> {
  if (this.stoptime == false) {
    this.stoptime = true;
  }
}

timerCycle=()=> {
    if (this.stoptime == false) {
    this.sec = parseInt(this.sec);
    this.min = parseInt(this.min);
    this.hr = parseInt(this.hr);

    this.sec = this.sec + 1;

    if (this.sec == 60) {
      this.min = this.min + 1;
      this.sec = 0;
    }
    if (this.min == 60) {
      this.hr = this.hr + 1;
      this.min = 0;
      this.sec = 0;
    }

    if (this.sec < 10 || this.sec == 0) {
      this.sec = '0' + this.sec;
    }
    if (this.min < 10 || this.min == 0) {
      this.min = '0' + this.min;
    }
    if (this.hr < 10 || this.hr == 0) {
      this.hr = '0' + this.hr;
    }

    this.timer = this.hr + ':' + this.min + ':' + this.sec;

    this.setTimeout("timerCycle()", 1000);
  }
}

resetTimer =()=> {
    this.timer = '00:00:00';
}

render (){
    return (
        <div className="stopwatch">
            <h2>Stopwatch</h2>
            <span className="stopwatch-time">0</span>
            <button className="" onClick={()=>this.handleStopwatch}>{this.state.isRunning ? "Stop" : "Start"}</button>
            <button className="" onClick={()=>this.stopTimer()}>Stop timer</button>
            <button className="" onClick={()=>this.resetTimer()}>Reset timer</button>
            <div className="stopwatch"></div>
            {/* <Counter id={props.id} index={props.index} score={props.score} incrementScore={props.incrementScore} decrementScore={props.decrementScore}/> */}
        </div>
    )

}


}
export default StopWatch
