import React from "react";

class StopWatch extends React.Component{
    state ={
        timer: document.querySelector('.stopwatch'),
        hr: 0,
        min: 0,
        sec : 0,
        stoptime : true

    }


startTimer =()=> {
  if (this.state.stoptime == true) {
        this.state.stoptime = false;
        this.timerCycle();
    }
}
stopTimer =()=> {
  if (this.stoptime == false) {
    this.stoptime = true;
  }
}

timerCycle=()=> {
    if (this.stoptime == false) {
    this.sec = parseInt(sec);
    min = parseInt(min);
    hr = parseInt(hr);

    sec = sec + 1;

    if (sec == 60) {
      min = min + 1;
      sec = 0;
    }
    if (min == 60) {
      hr = hr + 1;
      min = 0;
      sec = 0;
    }

    if (sec < 10 || sec == 0) {
      sec = '0' + sec;
    }
    if (min < 10 || min == 0) {
      min = '0' + min;
    }
    if (hr < 10 || hr == 0) {
      hr = '0' + hr;
    }

    timer.innerHTML = hr + ':' + min + ':' + sec;

    this.setTimeout("timerCycle()", 1000);
  }
}

resetTimer =()=> {
    timer.innerHTML = '00:00:00';
}

render (){
    return (
        <div className="player">
            <button className="" onClick={()=>this.startTimer()}>Start timer</button>
            <button className="" onClick={()=>this.stopTimer()}>Stop timer</button>
            <button className="" onClick={()=>this.resetTimer()}>Reset timer</button>
            <div className="stopwatch"></div>
            {/* <Counter id={props.id} index={props.index} score={props.score} incrementScore={props.incrementScore} decrementScore={props.decrementScore}/> */}
        </div>
    )

}


}
export default StopWatch
